import React from 'react';

const HowItWorks = () => {
  return (
    <section className="container mx-auto my-8 text-center">
      <h2 className="text-2xl font-bold mb-4">How it Works?</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="p-4 bg-gray-100 rounded-lg">
          <h3 className="font-bold">Step 1</h3>
          <p>Sign up and create your profile.</p>
        </div>
        <div className="p-4 bg-gray-100 rounded-lg">
          <h3 className="font-bold">Step 2</h3>
          <p>Post your requirements or browse suppliers.</p>
        </div>
        <div className="p-4 bg-gray-100 rounded-lg">
          <h3 className="font-bold">Step 3</h3>
          <p>Connect with suppliers and start your business.</p>
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
