import React, { useState } from 'react';
import API from '../services/api';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, Typography, Container, Box, Grid, Paper } from '@mui/material';
import styled from '@emotion/styled'; // Corrected import

// Styled components using Emotion
const StyledContainer = styled(Container)`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: #f4f6f9;
`;

const StyledPaper = styled(Paper)`
  padding: 30px;
  width: 100%;
  max-width: 400px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
  background: #fff;
`;

const StyledButton = styled(Button)`
  background-color: #3f51b5;
  color: #fff;
  &:hover {
    background-color: #303f9f;
  }
  width: 100%;
  padding: 10px;
`;

const Signup = () => {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({ username: '', password: '' });
  const navigate = useNavigate();

  const validateForm = () => {
    let isValid = true;
    const errors = {};

    // Username validation
    if (!formData.username) {
      errors.username = 'Username is required.';
      isValid = false;
    } else if (!/^[a-zA-Z0-9_]{3,}$/.test(formData.username)) {
      errors.username =
        'Username must be at least 3 characters long and contain only letters, numbers, or underscores.';
      isValid = false;
    }

    // Password validation
    if (!formData.password) {
      errors.password = 'Password is required.';
      isValid = false;
    } else if (
      !/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(formData.password)
    ) {
      errors.password =
        'Password must be at least 8 characters long and include uppercase, lowercase, number, and special character.';
      isValid = false;
    }

    setErrors(errors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        await API.post('/auth/signup', formData);
        alert('Signup successful! Please login.');
        navigate('/login');
      } catch (err) {
        alert('Signup failed. User might already exist.');
      }
    }
  };

  return (
    <StyledContainer>
      <StyledPaper>
        <Typography variant="h4" align="center" color="primary" gutterBottom>
          Sign Up
        </Typography>
        <form onSubmit={handleSubmit}>
          <Box mb={3}>
            <TextField
              label="Username"
              variant="outlined"
              fullWidth
              value={formData.username}
              onChange={(e) => setFormData({ ...formData, username: e.target.value })}
              error={!!errors.username}
              helperText={errors.username}
              required
            />
          </Box>
          <Box mb={3}>
            <TextField
              label="Password"
              type="password"
              variant="outlined"
              fullWidth
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              error={!!errors.password}
              helperText={errors.password}
              required
            />
          </Box>
          <StyledButton type="submit" variant="contained">
            Sign Up
          </StyledButton>
        </form>
        <Grid container justifyContent="flex-end" mt={2}>
          <Typography variant="body2">
            Already have an account? <a href="/login">Login</a>
          </Typography>
        </Grid>
      </StyledPaper>
    </StyledContainer>
  );
};

export default Signup;
