import React, { useState } from 'react';
import axios from 'axios';
import { Button, TextField, Typography, Box, Paper, Grid } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import styled from '@emotion/styled';

// Styled components using Emotion
const StyledContainer = styled(Box)`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: #f4f6f9;
`;

const StyledPaper = styled(Paper)`
  padding: 30px;
  width: 100%;
  max-width: 500px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
  background: #fff;
`;

const StyledButton = styled(Button)`
  background-color: #3f51b5;
  color: #fff;
  &:hover {
    background-color: #303f9f;
  }
  width: 100%;
  padding: 10px;
  margin-top: 20px;
`;
const StyledButton2 = styled(Button)`
  border-color: #3f51b5;
  color: #3f51b5;
  &:hover {
    background-color: #303f9f;
    color:
  }
  width: 100%;
  padding: 10px;
  margin-top: 20px;
`;

const WeatherSearch = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const goToReport = () => {
    navigate('/reports');
  };

  // Function to fetch weather data
  const fetchWeatherData = async () => {
    try {
      setError(''); // Clear previous errors
      const response = await axios.get(`http://api.weatherstack.com/current`, {
        params: {
          access_key: '613dc53cf06f24ca0c45f5802c257cfb', // Replace with your API key
          query: city, // City entered by user
        },
      });

      // Check if API response contains valid data
      if (response.data && response.data.current) {
        setWeatherData(response.data);
      } else {
        setError('Weather data not found. Please try again.');
      }
    } catch (err) {
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  return (
    <StyledContainer>
      <StyledPaper>
        <Typography variant="h4" align="center" color="primary" gutterBottom>
          Weather Search
        </Typography>

        {/* Input to Enter City */}
        <TextField
          label="Enter city name"
          variant="outlined"
          fullWidth
          value={city}
          onChange={(e) => setCity(e.target.value)}
          margin="normal"
        />

        {/* Button to Fetch Weather Data */}
        <StyledButton onClick={fetchWeatherData} variant="contained">
          Get Weather
        </StyledButton>

        {/* Button to View Reports */}
        <StyledButton2
          variant="outlined"
          color="secondary"
          onClick={goToReport}
          fullWidth
          sx={{ marginTop: '10px' }}
        >
          View Reports
        </StyledButton2>

        {/* Display Weather Data */}
        {weatherData && (
          <Box mt={3}>
            <Typography variant="h6" gutterBottom>
              Weather in {weatherData.location.name}, {weatherData.location.country}
            </Typography>
            <Typography variant="body1">
              <strong>Temperature:</strong> {weatherData.current.temperature}°C
            </Typography>
            <Typography variant="body1">
              <strong>Condition:</strong> {weatherData.current.weather_descriptions[0]}
            </Typography>
            <Typography variant="body1">
              <strong>Humidity:</strong> {weatherData.current.humidity}%
            </Typography>
            <img
              src={weatherData.current.weather_icons[0]}
              alt="weather icon"
              style={{ marginTop: '10px', width: '50px' }}
            />
          </Box>
        )}

        {/* Display Error Message */}
        {error && (
          <Typography variant="body2" color="error" align="center" mt={3}>
            {error}
          </Typography>
        )}
      </StyledPaper>
    </StyledContainer>
  );
};

export default WeatherSearch;
