import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Add your Tailwind CSS imports here

function App() {
  return (
    <div className="min-h-screen bg-blue-50">
      {/* Navbar */}
      <nav className="flex justify-between items-center px-6 py-4 shadow-md bg-white">
        <img src={habot} alt='habot'></img>
        <div className="space-x-6 text-lg">
          <a href="#" className="text-gray-700">Find Suppliers</a>
          <a href="#" className="text-gray-700">Find Service Tags</a>
          <button className="btn btn-success">Login / Sign Up</button>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="flex flex-col items-center justify-center text-center py-20 bg-blue-700 text-white">
        <h2 className="text-4xl font-bold mb-4">Are You a Supplier?</h2>
        <p className="text-2xl mb-8">Explore Matching Opportunities.</p>

        {/* Search Boxes */}
        <div className="flex gap-4 items-center max-w-2xl w-full px-4">
          <input
            type="text"
            placeholder="Search your required service here"
            className="form-control p-3 rounded-md shadow-sm flex-grow"
          />
          <input
            type="text"
            placeholder="Search your desired location here"
            className="form-control p-3 rounded-md shadow-sm flex-grow"
          />
          <button className="btn btn-success px-5 py-3">Search</button>
        </div>

        <p className="text-white mt-8">
          Are you a buyer? <a href="#" className="underline text-yellow-400">Click here if you are looking to post a requirement</a>
        </p>
      </section>
    </div>
  );
}

export default App;
