import React, { useState } from 'react';
import axios from 'axios';

const WeatherComponent = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState('');

  // Function to fetch weather data
  const fetchWeatherData = async () => {
    try {
      setError(''); // Clear previous errors
      const response = await axios.get(`http://api.weatherstack.com/current`, {
        params: {
          access_key: 'YOUR_WEATHERSTACK_API_KEY', // Replace with your API key
          query: city, // City entered by user
        },
      });

      // Check if API response contains valid data
      if (response.data && response.data.current) {
        setWeatherData(response.data);
      } else {
        setError('Weather data not found. Please try again.');
      }
    } catch (err) {
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Weather App</h1>

      {/* Input to Enter City */}
      <input
        type="text"
        placeholder="Enter city name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={fetchWeatherData}>Get Weather</button>

      {/* Display Weather Data */}
      {weatherData && (
        <div style={{ marginTop: '20px' }}>
          <h2>Weather in {weatherData.location.name}, {weatherData.location.country}</h2>
          <p><strong>Temperature:</strong> {weatherData.current.temperature}Â°C</p>
          <p><strong>Condition:</strong> {weatherData.current.weather_descriptions[0]}</p>
          <p><strong>Humidity:</strong> {weatherData.current.humidity}%</p>
          <img src={weatherData.current.weather_icons[0]} alt="weather icon" />
        </div>
      )}

      {/* Display Error Message */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default WeatherComponent;
