import React, { useState } from "react";
import wedding from "../assets/wedding.jpg";
import heading from "../assets/Heading.jpg";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    surname: "",
    mobile: "",
    email: "",
    gender: "",
    dob: "",
    maritalStatus: "Unmarried",
    qualification: "",
    height: "",
    religion: "",
    caste: "",
    profession: "",
    captcha: "",
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");

  const validateForm = () => {
    const newErrors = {};

    // Name validation
    if (!formData.name) {
      newErrors.name = "Name is required";
    }

    // Surname validation
    if (!formData.surname) {
      newErrors.surname = "Surname is required";
    }

    // Mobile number validation
    if (!formData.mobile) {
      newErrors.mobile = "Mobile number is required";
    } else if (!/^\d{10}$/.test(formData.mobile)) {
      newErrors.mobile = "Mobile number must be 10 digits";
    }

    // Email validation
    if (!formData.email) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Email is invalid";
    }

    // Gender validation
    if (!formData.gender) {
      newErrors.gender = "Gender is required";
    }

    // Date of Birth validation
    if (!formData.dob) {
      newErrors.dob = "Date of Birth is required";
    }

    // Qualification validation
    if (!formData.qualification) {
      newErrors.qualification = "Qualification is required";
    }

    // Height validation
    if (!formData.height) {
      newErrors.height = "Height is required";
    }

    // Religion validation
    if (!formData.religion) {
      newErrors.religion = "Religion is required";
    }

    // Caste validation
    if (!formData.caste) {
      newErrors.caste = "Caste is required";
    }

    // Profession validation
    if (!formData.profession) {
      newErrors.profession = "Profession is required";
    }

    // Captcha validation
    if (formData.captcha !== "OZH8M2") {
      newErrors.captcha = "Captcha is incorrect";
    }

    return newErrors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: "" }); // Clear error on change
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length === 0) {
      setSuccessMessage("Successfully registered!");
      setFormData({
        name: "",
        surname: "",
        mobile: "",
        email: "",
        gender: "",
        dob: "",
        maritalStatus: "Unmarried",
        qualification: "",
        height: "",
        religion: "",
        caste: "",
        profession: "",
        captcha: "",
      }); // Reset form
    } else {
      setErrors(validationErrors);
    }
  };

  return (
    <div className="min-h-screen bg-yellow-300">
      {/* Top login bar */}
      <div className="bg-yellow-300 flex justify-around items-center">
        <div>
          <img
            src={heading}
            alt="Marriage"
            className="object-cover"
            height={100}
            width={250}
          />
        </div>
        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Enter ID/Email/Mobile"
            className="border border-gray-300 p-1 rounded w-40"
          />
          <input
            type="password"
            placeholder="Enter password"
            className="border border-gray-300 p-1 rounded w-40"
          />
          <button className="bg-[#2A7E19] text-white px-2 py-1 rounded hover:bg-[#83ad7b]">
            Login
          </button>
          <a href="#" className="text-sm text-white hover:underline">
            Forgot password?
          </a>
        </div>
      </div>

      {/* Header */}
      <header className="bg-red-600 p-1 text-center">
        <div className="flex justify-around items-center">
          <div className="flex space-x-4 text-white">
            <a href="#" className="hover:underline">
              Home
            </a>
            <a href="#" className="hover:underline">
              About Us
            </a>
            <a href="#" className="hover:underline">
              Careers
            </a>
            <a href="#" className="hover:underline">
              Contact Us
            </a>
          </div>
          <div className="flex space-x-4 text-white">
            <a href="#" className="hover:underline">
              CMD Desk
            </a>
            <a href="#" className="hover:underline">
              Help
            </a>
          </div>
        </div>
      </header>

      {/* Main section with image and overlay form */}
      <div className="relative">
        {/* Background Image */}
        <img
          src={wedding}
          alt="Marriage"
          className="w-full h-[70vh] object-cover"
        />

        {/* Form overlay */}
        <div className="absolute top-0 right-0 bg-white p-4 shadow-lg w-[40vw] h-[80vh] overflow-y-auto m-2">
          <h2 className="text-xl font-bold mb-4">
            Register Free & Get Your Matched Partner
          </h2>
          <form onSubmit={handleSubmit} className="space-y-2">
            {/* Name and Surname */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Enter your name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                placeholder="Enter your name"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.name && <span className="text-red-600 text-sm">{errors.name}</span>}
            </div>
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Enter your surname</label>
              <input
                type="text"
                name="surname"
                value={formData.surname}
                placeholder="Enter your surname"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.surname && <span className="text-red-600 text-sm">{errors.surname}</span>}
            </div>

            {/* Mobile Number */}
            <div className="flex mb-2">
              <label className="mr-2 w-1/2">Enter mobile number</label>
              <input
                type="text"
                name="mobile"
                value={formData.mobile}
                placeholder="Enter mobile number"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.mobile && <span className="text-red-600 text-sm">{errors.mobile}</span>}
            </div>

            {/* Email */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Enter your email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                placeholder="Enter your email"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.email && <span className="text-red-600 text-sm">{errors.email}</span>}
            </div>

            {/* Gender Selection */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Gender</label>
              <div className="flex items-center space-x-4">
                <div className="flex items-center">
                  <input
                    type="radio"
                    name="gender"
                    value="male"
                    id="male"
                    onChange={handleChange}
                  />
                  <label htmlFor="male" className="ml-1">
                    Male
                  </label>
                </div>
                <div className="flex items-center">
                  <input
                    type="radio"
                    name="gender"
                    value="female"
                    id="female"
                    onChange={handleChange}
                  />
                  <label htmlFor="female" className="ml-1">
                    Female
                  </label>
                </div>
              </div>
              {errors.gender && <span className="text-red-600 text-sm">{errors.gender}</span>}
            </div>

            {/* Date of Birth */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Date of Birth</label>
              <input
                type="date"
                name="dob"
                value={formData.dob}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.dob && <span className="text-red-600 text-sm">{errors.dob}</span>}
            </div>

            {/* Marital Status */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Marital Status</label>
              <select
                name="maritalStatus"
                value={formData.maritalStatus}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              >
                <option value="Unmarried">Unmarried</option>
                <option value="Married">Married</option>
              </select>
            </div>

            {/* Qualification */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Qualification</label>
              <input
                type="text"
                name="qualification"
                value={formData.qualification}
                placeholder="Enter your qualification"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.qualification && <span className="text-red-600 text-sm">{errors.qualification}</span>}
            </div>

            {/* Height */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Height</label>
              <input
                type="text"
                name="height"
                value={formData.height}
                placeholder="Enter your height"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.height && <span className="text-red-600 text-sm">{errors.height}</span>}
            </div>

            {/* Religion */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Religion</label>
              <input
                type="text"
                name="religion"
                value={formData.religion}
                placeholder="Enter your religion"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.religion && <span className="text-red-600 text-sm">{errors.religion}</span>}
            </div>

            {/* Caste */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Caste</label>
              <input
                type="text"
                name="caste"
                value={formData.caste}
                placeholder="Enter your caste"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.caste && <span className="text-red-600 text-sm">{errors.caste}</span>}
            </div>

            {/* Profession */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Profession</label>
              <input
                type="text"
                name="profession"
                value={formData.profession}
                placeholder="Enter your profession"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.profession && <span className="text-red-600 text-sm">{errors.profession}</span>}
            </div>

            {/* Captcha */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Captcha (OZH8M2)</label>
              <input
                type="text"
                name="captcha"
                value={formData.captcha}
                placeholder="Enter Captcha"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.captcha && <span className="text-red-600 text-sm">{errors.captcha}</span>}
            </div>

            {/* Success Message */}
            {successMessage && <div className="text-green-600">{successMessage}</div>}

            {/* Submit Button */}
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Register
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegistrationForm;
