import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box, Paper } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const WeatherSearch = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const navigate = useNavigate();

  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState('');

 // Function to fetch weather data
 const handleSearch = async () => {
    try {
      setError(''); // Clear previous errors
      const response = await axios.get(`http://api.weatherstack.com/current`, {
        params: {
          access_key: '613dc53cf06f24ca0c45f5802c257cfb', // Replace with your API key
          query: city, // City entered by user
        },
      });

      // Check if API response contains valid data
      if (response.data && response.data.current) {
        setWeatherData(response.data);
      } else {
        setError('Weather data not found. Please try again.');
      }
    } catch (err) {
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  const goToReport = () => {
    navigate('/report');
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 10 }}>
      <Typography variant="h4" gutterBottom>
        Search Weather
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <TextField
          label="Enter City Name"
          variant="outlined"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          fullWidth
        />
        <Button variant="contained" color="primary" onClick={handleSearch}>
          Search
        </Button>
        <Button variant="outlined" color="secondary" onClick={goToReport}>
          View Report
        </Button>
      </Box>

      {weatherD && (
        <Paper elevation={3} sx={{ mt: 4, p: 3, textAlign: 'center' }}>
          <Typography variant="h5">Weather in {weather.city}</Typography>
          <Typography>Temperature: {weather.temperature}Â°C</Typography>
          <Typography>Condition: {weather.condition}</Typography>
        </Paper>
      )}
    </Container>
  );
};

export default WeatherSearch;
