import React, { useState } from "react";

const Matrix = () => {
  const [matrix, setMatrix] = useState(Array(3).fill(Array(3).fill("white")));
  const [clickedBoxes, setClickedBoxes] = useState([]);

  const handleBoxClick = (row, col) => {
    const updatedMatrix = matrix.map((rowArray, rIndex) =>
      rowArray.map((box, cIndex) => {
        if (rIndex === row && cIndex === col) return "green";
        return box;
      })
    );

    setMatrix(updatedMatrix);
    setClickedBoxes((prev) => [...prev, { row, col }]);

    // If it's the last box
    if (row === 2 && col === 2) {
      changeColorsSequentially();
    }
  };

  const changeColorsSequentially = () => {
    let delay = 0;
    clickedBoxes.forEach(({ row, col }) => {
      setTimeout(() => {
        setMatrix((prevMatrix) =>
          prevMatrix.map((rowArray, rIndex) =>
            rowArray.map((box, cIndex) => {
              if (rIndex === row && cIndex === col) return "orange";
              return box;
            })
          )
        );
      }, delay);
      delay += 500; // 500ms delay between each color change
    });
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: "15px",
        marginTop: "50px",
        fontFamily: "'Arial', sans-serif",
      }}
    >
      <h1 style={{ color: "#333", fontSize: "24px", marginBottom: "20px" }}>
        3x3 Interactive Matrix
      </h1>
      {matrix.map((row, rowIndex) => (
        <div key={rowIndex} style={{ display: "flex", gap: "15px" }}>
          {row.map((color, colIndex) => (
            <div
              key={colIndex}
              onClick={() => handleBoxClick(rowIndex, colIndex)}
              style={{
                width: "60px",
                height: "60px",
                backgroundColor: color,
                borderRadius: "12px",
                boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                border: "2px solid #ddd",
                cursor: "pointer",
                transition: "all 0.3s ease",
                fontWeight: "bold",
                color: "#fff",
              }}
              onMouseEnter={(e) => (e.target.style.transform = "scale(1.1)")}
              onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
            >
              {rowIndex === 2 && colIndex === 2 ? "Last" : ""}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Matrix;
