import React, { useState } from "react";

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    surname: "",
    mobile: "",
    email: "",
    gender: "Male", // default gender
    dob: "",
    maritalStatus: "Unmarried", // default marital status
    qualification: "",
    height: "",
    religion: "",
    caste: "",
    profession: "",
    captcha: "",
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validate = () => {
    const validationErrors = {};
    if (!formData.name) validationErrors.name = "Name is required";
    if (!formData.surname) validationErrors.surname = "Surname is required";
    if (!formData.mobile) validationErrors.mobile = "Mobile number is required";
    if (!formData.email) validationErrors.email = "Email is required";
    if (!formData.dob) validationErrors.dob = "Date of birth is required";
    if (!formData.qualification) validationErrors.qualification = "Qualification is required";
    if (!formData.height) validationErrors.height = "Height is required";
    if (!formData.religion) validationErrors.religion = "Religion is required";
    if (!formData.caste) validationErrors.caste = "Caste is required";
    if (!formData.profession) validationErrors.profession = "Profession is required";
    if (!formData.captcha) validationErrors.captcha = "Captcha is required";

    setErrors(validationErrors);
    return Object.keys(validationErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      // Submit form data (here you would normally send it to an API)
      setSuccessMessage("Registration successful!");
      setFormData({
        name: "",
        surname: "",
        mobile: "",
        email: "",
        gender: "Male",
        dob: "",
        maritalStatus: "Unmarried",
        qualification: "",
        height: "",
        religion: "",
        caste: "",
        profession: "",
        captcha: "",
      });
      setErrors({});
    }
  };

  return (
    <div className="p-4">
      <div className="max-w-lg mx-auto">
        <h2 className="text-lg font-bold mb-4">Registration Form</h2>
        <div className="border p-4 rounded shadow">
          <form onSubmit={handleSubmit}>
            {/* Name */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.name && <span className="text-red-600 text-sm">{errors.name}</span>}
            </div>

            {/* Surname */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Surname</label>
              <input
                type="text"
                name="surname"
                value={formData.surname}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.surname && <span className="text-red-600 text-sm">{errors.surname}</span>}
            </div>

            {/* Mobile Number */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Mobile Number</label>
              <input
                type="text"
                name="mobile"
                value={formData.mobile}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.mobile && <span className="text-red-600 text-sm">{errors.mobile}</span>}
            </div>

            {/* Email */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.email && <span className="text-red-600 text-sm">{errors.email}</span>}
            </div>

            {/* Gender */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Gender</label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              >
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </div>

            {/* Date of Birth */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Date of Birth</label>
              <input
                type="date"
                name="dob"
                value={formData.dob}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.dob && <span className="text-red-600 text-sm">{errors.dob}</span>}
            </div>

            {/* Marital Status */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Marital Status</label>
              <select
                name="maritalStatus"
                value={formData.maritalStatus}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              >
                <option value="Unmarried">Unmarried</option>
                <option value="Married">Married</option>
              </select>
              {errors.maritalStatus && <span className="text-red-600 text-sm">{errors.maritalStatus}</span>}
            </div>

            {/* Qualification */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Qualification</label>
              <select
                name="qualification"
                value={formData.qualification}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              >
                <option value="">Select Qualification</option>
                <option value="High School">High School</option>
                <option value="Bachelor's Degree">Bachelor's Degree</option>
                <option value="Master's Degree">Master's Degree</option>
                <option value="PhD">PhD</option>
              </select>
              {errors.qualification && <span className="text-red-600 text-sm">{errors.qualification}</span>}
            </div>

            {/* Height */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Height</label>
              <input
                type="text"
                name="height"
                value={formData.height}
                placeholder="Enter your height"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.height && <span className="text-red-600 text-sm">{errors.height}</span>}
            </div>

            {/* Religion */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Religion</label>
              <input
                type="text"
                name="religion"
                value={formData.religion}
                placeholder="Enter your religion"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.religion && <span className="text-red-600 text-sm">{errors.religion}</span>}
            </div>

            {/* Caste */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Caste</label>
              <input
                type="text"
                name="caste"
                value={formData.caste}
                placeholder="Enter your caste"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.caste && <span className="text-red-600 text-sm">{errors.caste}</span>}
            </div>

            {/* Profession */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Profession</label>
              <input
                type="text"
                name="profession"
                value={formData.profession}
                placeholder="Enter your profession"
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.profession && <span className="text-red-600 text-sm">{errors.profession}</span>}
            </div>

            {/* Captcha */}
            <div className="flex mb-2">
              <label className="w-1/2 mr-2">Captcha</label>
              <input
                type="text"
                name="captcha"
                value={formData.captcha}
                onChange={handleChange}
                className="flex-1 p-1 border border-gray-300 rounded"
              />
              {errors.captcha && <span className="text-red-600 text-sm">{errors.captcha}</span>}
            </div>

            <button
              type="submit"
              className="mt-4 bg-blue-600 text-white py-2 px-4 rounded"
            >
              Register
            </button>
          </form>
          {successMessage && <div className="mt-4 text-green-600">{successMessage}</div>}
        </div>
      </div>
    </div>
  );
};

export default RegistrationForm;
