import React, { useEffect, useState } from 'react';
import { Container, Typography, Paper, List, ListItem, ListItemText, Box, CircularProgress } from '@mui/material';
import API from '../services/api';
import { styled } from '@emotion/styled';

// Styled components using Emotion
const StyledPaper = styled(Paper)`
  padding: 20px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
  background-color: #ffffff;
`;

const Report = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const response = await API.get('/reports');
        setReports(response.data);
      } catch (err) {
        alert('Error fetching report');
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, []);

  return (
    <Container maxWidth="md" sx={{ mt: 10 }}>
      <Typography variant="h4" align="center" gutterBottom>
        Weather Search Report
      </Typography>

      {/* Loading indicator */}
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <StyledPaper elevation={3}>
          <List>
            {reports.length === 0 ? (
              <Typography variant="h6" align="center" sx={{ mt: 2 }}>
                No reports found.
              </Typography>
            ) : (
              reports.map((report, index) => (
                <ListItem key={index} divider sx={{ '&:hover': { backgroundColor: '#f1f1f1' } }}>
                  <ListItemText
                    primary={<Typography variant="h6">{`${report.username} searched for ${report.city}`}</Typography>}
                    secondary={
                      <Typography variant="body2">
                        Temperature: {report.temperature}Â°C, Condition: {report.condition}
                      </Typography>
                    }
                  />
                </ListItem>
              ))
            )}
          </List>
        </StyledPaper>
      )}
    </Container>
  );
};

export default Report;
